        if(rarity == LegionRarity.COMMON) {
            floorType = FloorType.SUBFLOOR1;
        } else if(rarity == LegionRarity.UNCOMMON) {
            floorType = FloorType.SUBFLOOR2;
        } else if(rarity == LegionRarity.RARE) {
            floorType = FloorType.SUBFLOOR3;
        }
